import React, { Component } from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import GroupService from '../../api/todo/GroupService.js'
import AuthenticationService from './AuthenticationService.js'

class CreateGroupComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            groupName: '',
            courseId: '',
            courseName: ''
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)

    }

    componentDidMount() {

        if (this.state.id === -1) {
            return
        }
    }

    validate(values) {
        let errors = {}
        if (!values.groupName) {
            errors.groupName = 'Enter a Group Name'
        } 

        if (!values.courseId) {
            errors.courseId = 'Enter Course ID'
        } 

        if (!values.courseName) {
            errors.courseName = 'Enter Course Name'
        } 

        return errors

    }

    onSubmit(values) {
        let username = AuthenticationService.getLoggedInUserName()

        let group = {
            id: this.state.id,
            groupName: values.groupName,
            courseId: values.courseId,
            courseName: values.courseName
        }

            GroupService.createGroup(username, group)
                .then(() => this.props.history.push(`/welcome/${AuthenticationService.getLoggedInUserName()}`))
        
        console.log(values);
    }

    render() {

        let { groupName, courseId, courseName } = this.state
        //let targetDate = this.state.targetDate

        return (
            <div>
                <h1>Todo</h1>
                <div className="container">
                    <Formik
                        initialValues={{ groupName, courseId, courseName }}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="groupName" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="courseName" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="courseId" component="div"
                                        className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Group Name</label>
                                        <Field className="form-control" type="text" name="groupName" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Course ID</label>
                                        <Field className="form-control" type="text" name="courseId" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Course Name</label>
                                        <Field className="form-control" type="text" name="courseName" />
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Create</button>
                                </Form>
                            )
                        }
                    </Formik>

                </div>
            </div>
        )
    }
}

export default CreateGroupComponent
