import React, { Component } from 'react'
import moment from 'moment'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import CourseDataService from '../../api/todo/CourseDataService.js'
import MenteeDataService from '../../api/todo/MenteeDataService.js'
import AuthenticationService from './AuthenticationService.js'
import Select from 'react-select';

const options = [
    { value: 'A1', label: 'A1' },
    { value: 'A2', label: 'A2' },
    { value: 'A3', label: 'A3' },
  ];

class MenteeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            courses: [],
            id: this.props.match.params.id,
            courseId: '',
            coursename: '',
            mentorDate: moment(new Date()).format('YYYY-MM-DD')
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)

    }

    componentDidMount() {

        // if (this.state.id === -1) {
        //     return
        // }

        let username = AuthenticationService.getLoggedInUserName()
        options.length=0
        for(let i=1;i<=1;i++)
        {
            CourseDataService.retrieveAllCourses(username) 
            .then(response => this.setState({
                courses: response.data,
                })
            )
                //hardcoded
            options.push( {value: "BP094", label: "SEPT"})
            options.push( {value: "COSC1234", label: "Intro to Programming"})
            options.push( {value: "COSC2269", label: "Web Programming"})
            options.push( {value: "COSC5879", label: "Algorithms"})
            // options.push( {value: `${this.state.courseId}`, label: `${this.state.coursename}`})
        }
    }

    validate(values) {
        let errors = {}
        if (!values.courseId) {
            errors.courseId = 'Enter Course ID'
        }

        if (!moment(values.mentorDate).isValid()) {
            errors.mentorDate = 'Enter a valid Mentoring Date'
        }

        return errors
    }

    onSubmit(values) {
        let username = AuthenticationService.getLoggedInUserName()

        let mentee = {
            id: this.state.id,
            courseId: values.courseId.value,
            courseName: values.courseId.label,
            mentorDate: values.mentorDate
        }

        if (this.state.id === -1) {
            MenteeDataService.createMentee(username, mentee)
                .then(() => this.props.history.push('/mentees'))
        } else {
            MenteeDataService.updateMentee(username, this.state.id, mentee)
                .then(() => this.props.history.push('/mentees'))
        }

        console.log(values);
    }
    
    handleChange = courseId => {
        this.setState({ courseId });
    };

    render() {

        let { courseId,mentorDate} = this.state
        //let targetDate = this.state.targetDate
        
        return (
            <div>
                <h1>Mentee Application</h1>
                <div className="container">
                    <Formik
                        initialValues={{ courseId,mentorDate}}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="courseId" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="courseName" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="mentorDate" component="div"
                                        className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Course ID</label>
                                        <Select 
                                            value={courseId}
                                            onChange={this.handleChange}
                                            options={options}
                                            name="courseId"
                                            />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Course Name</label>
                                        <Field className="form-control" type="text" name="courseName" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Mentoring Date</label>
                                        <Field className="form-control" type="date" name="mentorDate" />
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Apply</button>
                                </Form>
                            )
                        }
                    </Formik>

                </div>
            </div>
        )
    }
}

export default MenteeComponent
