import React, { Component } from 'react'
import MenteeDataService from '../../api/todo/MenteeDataService.js'
import AuthenticationService from './AuthenticationService.js'
import moment from 'moment'
import Popup from "reactjs-popup"
import { Formik, Form, Field, ErrorMessage } from 'formik';
import MentorDataService from '../../api/todo/MentorDataService.js'

class ListMenteesComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            mentees: [],
            message: null,
            rating: '',
            input:'',
            
            mentorId:'',
            mentorName:'',
            courseId:'',
            courseName:'',
            startDate:'',
            totalRate:0,
            numOfMentee:0
        }
        this.deleteMenteeClicked = this.deleteMenteeClicked.bind(this)
        this.updateMenteeClicked = this.updateMenteeClicked.bind(this)
        this.addMenteeClicked = this.addMenteeClicked.bind(this)
        this.refreshMentees = this.refreshMentees.bind(this)
        this.updateRating = this.updateRating.bind(this)
        this.getMentor = this.getMentor.bind(this)
        this.searchMentorClicked = this.searchMentorClicked.bind(this)
    }

    componentWillUnmount() {
    }

    shouldComponentUpdate(nextProps, nextState) {
        return true
    }

    componentDidMount() {
        this.refreshMentees();
    }

    //refresh the page when something is updated, clicked, and deleted
    refreshMentees() {
        let username = AuthenticationService.getLoggedInUserName()
        MenteeDataService.retrieveAllMentees(username)
            .then(
                response => {
                    this.setState({ mentees: response.data })
                }
            )
    }

    //what happens when delete button is clicked
    deleteMenteeClicked(id) {
        let username = AuthenticationService.getLoggedInUserName()

        MenteeDataService.deleteMentee(username, id)
            .then(
                response => {
                    this.setState({ message: `Delete of mentee ${id} Successful` })
                    this.refreshMentees()
                }
            )
    }

    //what happens when update button is clicked
    updateMenteeClicked(id) {
        this.props.history.push(`update/mentees/${id}`)
    }

    //what happens when add button is clicked
    addMenteeClicked() {
        this.props.history.push(`/mentees/-1`)
    }

    searchMentorClicked(){
        this.props.history.push(`/mentees/${input}`)
    }

    //what happend when submit button is clicked
    updateRating(id, courseId, mentorName){
        let username = AuthenticationService.getLoggedInUserName()

        if(this.state.rating!="") {
            
            // let mentor = {
            //     id:this.state.mentorId,
            //     username:this.state.mentorName,
            //     courseId:this.state.courseId,
            //     courseName:this.state.courseName,
            //     startdate:this.state.startDate,
            //     totalRate:this.state.totalRate,
            //     numberOfMentee:this.state.numOfMentee
            // }

            this.deleteMenteeClicked(id)
        }
        else{
            alert("Please input a value for rating")
        }
    }

    //handle the change input for rating 
    handleChange = (e) => {
        this.setState({
            rating:e.target.value});
    };

    //for some reason it goes into here but doesnt set anything ????????
    getMentor(mentorname, mentorid) {
        // MentorDataService.setRate(mentorname, mentorid, this.state.rating)
        
        // MentorDataService.retrieveMentor(mentorname, mentorid)
        //     .then(response=>({
        //         mentorId:mentorid,
        //         mentorName:mentorname,
        //         courseId:response.data.courseId,
        //         courseName:response.data.courseName,
        //         startDate:moment(response.data.startdate).format('YYYY-MM-DD'),
        //         totalRate:response.data.rate,
        //         numOfMentee:response.data.numberOfMentee
        //     }))
    }

    //what to print out
    render() {        
        return (
            <div>
                <h1>Apply As Mentee</h1>
                {this.state.message && <div class="alert alert-success">{this.state.message}</div>}
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Course ID</th>
                                <th>Course Name</th>
                                <th>Date</th>
                                <th>Mentor Name</th>
                                <th>Mentor Email</th>
                                <th>IsCompleted?</th>
                                <th>Update</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.mentees.map(
                                    mentee =>
                                        <tr key={mentee.id}>
                                            <td>{mentee.courseId}</td>
                                            <td>{mentee.courseName}</td>
                                            <td>{moment(mentee.mentorDate).format('YYYY-MM-DD')}</td>
                                            <td>
                                                <Popup trigger={<a>{mentee.mentorName}</a>}>
                                                    <p>Mentor ID</p>
                                                    <p>{mentee.mentorID}</p> 
                                                    <p>Mentor Email</p>
                                                    <p>{mentee.mentorEmail}</p> 
                                                </Popup>
                                            </td>
                                            <td>{mentee.isCompleted.toString()}</td>
                                            <td><button className="btn btn-success" onClick={() => this.updateMenteeClicked(mentee.id)}>Update</button></td>
                                            <td>
                                                {!mentee.isCompleted && <button className="btn btn-warning" onClick={() => this.deleteMenteeClicked(mentee.id)}>Cancel</button>}
                                                {mentee.isCompleted && 
                                                    <Popup trigger={<button className="btn btn-warning" onClick={() => this.onClickUpdate(mentee.courseId, mentee.mentorName)}>Delete</button>}>
                                                        <Formik>
                                                            <Form>
                                                                <fieldset className="form-group">
                                                                    <label>Rating</label>
                                                                    <Field className="form-control" onChange={this.handleChange.bind(this)} onFocus={() => this.getMentor(mentee.mentorName, mentee.mentorID)} type="number" name="rating" min="0" max="5"/>
                                                                </fieldset>
                                                                <button className="btn btn-success" onClick={() => this.updateRating(mentee.id, mentee.mentorName)}>Submit</button>
                                                            </Form>
                                                        </Formik>
                                                    </Popup>}
                                            </td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                    <div className="row">
                        <fieldset className="form-group">
                            <button className="btn btn-success" onClick={this.addMenteeClicked}>Apply</button>
                        </fieldset>
                    </div>
                    <Formik>
                        <Form>
                            <div className="row">
                                <fieldset className="form-group">
                                    <Field name="mentorName" placeholder="Input mentor's name" className="form-control" type="text"/>
                                </fieldset>
                            </div>
                            <div className="row">
                                <button className="btn btn-success" onClick={this.searchMentorClicked}>Search</button>
                            </div>
                        </Form>
                    </Formik>
                </div>
            </div>
        )
    }
}

export default ListMenteesComponent
