import React, { Component } from 'react'
import moment from 'moment'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import MenteeDataService from '../../api/todo/MenteeDataService.js'
import AuthenticationService from './AuthenticationService.js'

class MenteeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            courseId: '',
            coursename: '',
            mentorDate: moment(new Date()).format('YYYY-MM-DD')
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)

    }

    componentDidMount() {

        if (this.state.id === -1) {
            return
        }

        let username = AuthenticationService.getLoggedInUserName()

        MenteeDataService.retrieveMentee(username, this.state.id)
            .then(response => this.setState({
                courseID: response.data.courseID,
                targetDate: moment(response.data.targetDate).format('YYYY-MM-DD')
            }))
    }

    validate(values) {
        let errors = {}
        if (!values.courseID) {
            errors.courseID = 'Enter Course ID'
        }
        if (!moment(values.targetDate).isValid()) {
            errors.targetDate = 'Enter a valid Target Date'
        }

        return errors

    }

    onSubmit(values) {
        let username = AuthenticationService.getLoggedInUserName()

        let mentee = {
            id: this.state.id,
            courseID: values.courseID,
            targetDate: values.targetDate
        }

        if (this.state.id === -1) {
            MenteeDataService.createMentee(username, mentee)
                .then(() => this.props.history.push('/mentees'))
        } else {
            MenteeDataService.updateMentee(username, this.state.id, mentee)
                .then(() => this.props.history.push('/mentees'))
        }

        console.log(values);
    }

    render() {

        let { courseID, targetDate} = this.state
        //let targetDate = this.state.targetDate
        
        return (
            <div>
                <h1>Mentee Application</h1>
                <div className="container">
                    <Formik
                        initialValues={{ courseID}}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="courseID" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="targetDate" component="div"
                                        className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Course ID</label>
                                        <Field className="form-control" type="text" name="courseID" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Target Date</label>
                                        <Field className="form-control" type="date" name="targetDate" />
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Save</button>
                                </Form>
                            )
                        }
                    </Formik>

                </div>
            </div>
        )
    }
}

export default MenteeComponent
