import React, { Component } from 'react'
import GroupDataService from '../../api/todo/GroupService.js'
import AuthenticationService from './AuthenticationService.js'
import moment from 'moment'

class ListJoinGroupsComponent extends Component {
    constructor(props) {
        console.log('constructor')
        super(props)
        this.state = {
            groups: [],
            message: null
        }
        this.deleteGroupClicked = this.deleteGroupClicked.bind(this)
        this.updateGroupClicked = this.updateGroupClicked.bind(this)
        this.createStudyGroupClicked = this.createStudyGroupClicked.bind(this)
        this.joinStudyGroupClicked = this.joinStudyGroupClicked.bind(this)
        this.refreshGroups = this.refreshGroups.bind(this)
    } 

    componentWillUnmount() {
        console.log('componentWillUnmount')
    }

    shouldComponentUpdate(nextProps, nextState) {
        console.log('shouldComponentUpdate')
        console.log(nextProps)
        console.log(nextState)
        return true
    }

    componentDidMount() {
        console.log('componentDidMount')
        this.refreshGroups();
        console.log(this.state)
    }

    refreshGroups() {
        let username = AuthenticationService.getLoggedInUserName()
        GroupDataService.retrieveAllGroups(username)
            .then(
                response => {
                    //console.log(response);
                    this.setState({ groups: response.data })
                }
            )
    }

    deleteGroupClicked(id) {
        let username = AuthenticationService.getLoggedInUserName()
        //console.log(id + " " + username);
        GroupDataService.deleteGroup(username, id)
            .then(
                response => {
                    this.setState({ message: `Delete of group ${id} Successful` })
                    this.refreshGroups()
                }
            )

    }

    joinStudyGroupClicked() {
        this.props.history.push(`/joingroups/-1`)
    }

    createStudyGroupClicked() {
        this.props.history.push(`/creategroups/-1`)
    }

    updateGroupClicked(id) {
        console.log('update ' + id)
        this.props.history.push(`/joingroups/${id}`)
        // /mentors/${id}
        // let username = AuthenticationService.getLoggedInUserName()
        // //console.log(id + " " + username);
        // GroupDataService.deleteGroup(username, id)
        //  .then (
        //      response => {
        //         this.setState({message : `Delete of mentor ${id} Successful`})
        //         this.refreshGroups()
        //      }
        //  )

    }

    render() {
        console.log('render')
        return (
            <div>
                <h1>Study Groups</h1>
                {this.state.message && <div class="alert alert-success">{this.state.message}</div>}
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Group Name</th>
                                <th>Course ID</th>
                                <th>Course Name</th>
                                <th>Number of Member</th>
                                <th>Start Date</th>
                                <th>Leave</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.groups.map(
                                    group =>
                                        <tr key={group.id}>
                                            <td>{group.groupname}</td>
                                            <td>{group.courseid}</td>
                                            <td>{group.coursename}</td>
                                            <td>{group.numberOfMember}</td>
                                            <td>{group.startdate}</td>
                                            <td><button className="btn btn-warning" onClick={() => this.deleteGroupClicked(group.id)}>Leave</button></td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                    <div className="row">
                        <button className="btn btn-success" onClick={this.createStudyGroupClicked}>Create</button>   
                        <button className="btn btn-success" onClick={this.joinStudyGroupClicked} style={({ marginLeft: '0.8rem' })} >Join</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default ListJoinGroupsComponent
