import React, { Component } from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import GroupService from '../../api/todo/GroupService.js'
import CourseDataService from '../../api/todo/CourseDataService.js'
import AuthenticationService from './AuthenticationService.js'

class JoinGroupComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            courses: [],
            id: this.props.match.params.id,
            groupName:'',
            courseId: '',
            courseName: '',
            numberOfMember:''
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)

    }

    componentDidMount() {
        let username = AuthenticationService.getLoggedInUserName()
        CourseDataService.retrieveAllCourses(username) 
        .then(response => this.setState({courses: response.data})
        )
    }

    validate(values) {
        let errors = {}
        if (!values.groupName) {
            errors.groupName = 'Enter Group Name'
        }
        if (!values.courseId) {
            errors.courseId = 'Enter Course ID'
        }

        return errors

    }

    onSubmit(values) {
        let username = AuthenticationService.getLoggedInUserName()

        let group = {
            id: this.state.id,
            groupName: values.groupName,
            courseId: values.courseId,
            courseName: values.courseName,
            numberOfMember: values.numberOfMember
        }

        GroupService.createGroup(username, group)
            .then(() => this.props.history.push('/groups'))
    }

    handleCourseChange = (e) => {
        let index = e.nativeEvent.target.selectedIndex;
        let label = e.nativeEvent.target[index].text;
        let value = e.target.value;

        this.setState({
            courseId:value, coursename:label});
    };
    
    render() {

        let { groupName,courseId,courseName,numberOfMember } = this.state
        
        return (
            <div>
                <h1>Study Group Application</h1>
                <div className="container">
                    <Formik
                        initialValues={{ groupName,courseId,courseName,numberOfMember }}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="groupName" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="courseId" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="courseName" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="numberOfMember" component="div"
                                        className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Course</label>
                                        <select
                                            className="form-control"
                                            onChange={this.handleCourseChange.bind(this)}
                                            value={this.state.courseId}
                                            name="courseId"
                                        >
                                            <option value=''>Select a course</option>
                                            {
                                                this.state.courses.map(
                                                    course =>
                                                        <option key={course.id} value={course.courseId}>{course.coursename}</option>
                                                )
                                            }
                                        </select>
                                    </fieldset>
                                     <fieldset className="form-group">
                                        <label>Group</label>
                                        <select
                                            className="form-control"
                                            // create new methods for onchange n onblur
                                            onChange={this.handleGroupChange.bind(this)}
                                            value={this.state.group}
                                            name="mentor"
                                        >
                                            <option value=''>Select a group</option>
                                            {
                                                this.state.groups.map(
                                                    group =>
                                                        //value is mentor's email n label is mentor's name
                                                        <option key={mentor.id} value={mentor.email}>{mentor.username}</option>
                                                )
                                            }
                                        </select>
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Group Name</label>
                                        <Field className="form-control" type="text" name="groupName" />
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Join</button>
                                </Form>
                            )
                        }
                    </Formik>

                </div>
            </div>
        )
    }
}

export default JoinGroupComponent
