import React, { Component } from 'react'
import moment from 'moment'
import { Formik, Form, ErrorMessage } from 'formik';
import CourseDataService from '../../api/todo/CourseDataService.js'
import MentorDataService from '../../api/todo/MentorDataService.js'
import AuthenticationService from './AuthenticationService.js'
import Select from 'react-select';

let username = AuthenticationService.getLoggedInUserName()
const options = [];

  CourseDataService.retrieveAllCourses(username)

class MentorComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            courses: [],
            id: this.props.match.params.id,
            courseId: null,
            coursename: '',
            targetDate: moment(new Date()).format('YYYY-MM-DD')
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)

    }

    componentDidMount() {
        let username = AuthenticationService.getLoggedInUserName()
        options.length=0
        CourseDataService.retrieveAllCourses(username) 
        .then(response => this.setState({
            courses: response.data,
            })
        )
        for(let i=0; i<courses.length;i++) {
            let getCourse=courses[i]
            let course = {
                courseId: getCourse.courseId,
                courseName: getCourse.courseId
            }
            options.push( {value: `${course.courseId}`, label: `${this.state.coursename}`})
        }
            

            //hardcoded
        options.push( {value: "BP094", label: "SEPT"})
        options.push( {value: "COSC1234", label: "Intro to Programming"})
        options.push( {value: "COSC2269", label: "Web Programming"})
        options.push( {value: "COSC5879", label: "Algorithms"})
        
    }

    validate(values) {
        let errors = {}
        if (!values.courseId.value) {
            errors.courseId = 'Enter Course ID'
        }

        // if (!values.courseName) {
        //     errors.courseName = 'Enter Course Name'
        // }

        return errors

    }

    handleChange = courseId => {
        this.setState({ courseId });
    };

    onSubmit(values) {
        let username = AuthenticationService.getLoggedInUserName()

        let mentor = {
            id: this.state.id,
            courseId: values.courseId.value,
            courseName: values.courseId.label,
            startdate: values.startdate
        }
        
        if (this.state.id === -1) {
            MentorDataService.createMentor(username, mentor)
                .then(() => this.props.history.push('/mentors'))
        } else {
            MentorDataService.updateMentor(username, this.state.id, mentor)
                .then(() => this.props.history.push('/mentors'))
        }

        console.log(values);
    }

    render() {

        let { courseId,startdate } = this.state
        
        return (
            <div>
                <h1>Mentor Application</h1>
                <div className="container">
                    <Formik
                        initialValues={{ courseId,startdate }}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="courseId" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="courseName" component="div"
                                    className="alert alert-warning" />
                                    <ErrorMessage name="startdate" component="div"
                                    className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Course ID</label>
                                        <Select 
                                            value={courseId}
                                            onChange={this.handleChange}
                                            options={options}
                                            name="courseId"
                                            />
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Apply</button>
                                </Form>
                            )
                        }
                    </Formik>

                </div>
            </div>
        )
    }
}

export default MentorComponent
