import React, { Component } from 'react'
import AuthenticationService from './AuthenticationService.js'
import GoogleLogin from 'react-google-login'
class LoginComponent extends Component {

    constructor(props) {
        super(props)
        this.responseGoogle = this.responseGoogle.bind(this)
    }

    responseGoogle(response) {
        // console.log("RESPONSE", response);
        // console.log("RESPONSE", response.profileObj.givenName);
        // console.log("RESPONSE", response.profileObj.email);
        // AuthenticationService.oauthGoogle(response.accessToken);
        AuthenticationService
        .executeBasicAuthenticationService()
        .then(() => {
            AuthenticationService.registerSuccessfulLogin()
            this.props.history.push(`/welcome/${response.profileObj.givenName}`)
        }).catch( () =>{
            this.setState({showSuccessMessage:false})
            this.setState({hasLoginFailed:true})
        })
        AuthenticationService
            .executeJwtAuthenticationService()
            .then((response) => {
                AuthenticationService.registerSuccessfulLoginForJwt()
                this.props.history.push(`/welcome/${response.profileObj.givenName}`)
            }).catch(() => {
                this.setState({ showSuccessMessage: false })
                this.setState({ hasLoginFailed: true })
            })
    }

    render() {
        return (
            <div>
                <h1>Login</h1>
                {/* <GoogleLogin
                    clientId="523292681676-qqq33easshjp33p16ou9hk4buhh17enf.apps.googleusercontent.com"
                    buttonText="Login"
                    onSuccess={this.responseGoogle}
                    onFailure={this.responseGoogle}
                    cookiePolicy={'single_host_origin'}
                 /> */}
                 <div className="container">
                    {/*<ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed}/>*/}
                    {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credentials or something is wrong</div>}
                    {this.state.showSuccessMessage && <div>Login Sucessful</div>}
                    {/*<ShowLoginSuccessMessage showSuccessMessage={this.state.showSuccessMessage}/>*/}
                    User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                    Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                </div>
            </div>
        )
    }
}

export default LoginComponent
