import React, { Component } from 'react'
import moment from 'moment'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import MentorDataService from '../../api/todo/MentorDataService.js'
import AuthenticationService from './AuthenticationService.js'

class MentorComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            courseId: '',
            coursename: '',
            targetDate: moment(new Date()).format('YYYY-MM-DD')
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)

    }

    componentDidMount() {

        if (this.state.id === -1) {
            return
        }

        let username = AuthenticationService.getLoggedInUserName()

        MentorDataService.retrieveMentor(username, this.state.id)
            .then(response => this.setState({
                courseId: response.data.courseId,
                courseName: response.data.courseName,
                startdate:  moment(response.data.startdate).format('YYYY-MM-DD')
            }))
    }

    validate(values) {
        let errors = {}
        if (!values.courseId) {
            errors.courseId = 'Enter Course ID'
        }

        if (!values.courseName) {
            errors.courseName = 'Enter Course Name'
        }

        if (!moment(values.startdate).isValid()) {
            errors.startdate = 'Enter a valid Date'
        }
        return errors

    }

    onSubmit(values) {
        let username = AuthenticationService.getLoggedInUserName()

        let mentor = {
            id: this.state.id,
            courseId: values.courseId,
            courseName: values.courseName,
            startdate: values.startdate
        }

        if (this.state.id === -1) {
            MentorDataService.createMentor(username, mentor)
                .then(() => this.props.history.push('/mentors'))
        } else {
            MentorDataService.updateMentor(username, this.state.id, mentor)
                .then(() => this.props.history.push('/mentors'))
        }

        console.log(values);
    }

    render() {

        let { courseId,courseName,startdate } = this.state
        
        return (
            <div>
                <h1>Mentor Application</h1>
                <div className="container">
                    <Formik
                        initialValues={{ courseId,courseName,startdate }}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="courseId" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="coursename" component="div"
                                    className="alert alert-warning" />
                                    <ErrorMessage name="startdate" component="div"
                                    className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Course ID</label>
                                        <Field className="form-control" type="text" name="courseId" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Course Name</label>
                                        <Field className="form-control" type="text" name="coursename" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Target Date</label>
                                        <Field className="form-control" type="date" name="startdate" />
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Apply</button>
                                </Form>
                            )
                        }
                    </Formik>

                </div>
            </div>
        )
    }
}

export default MentorComponent
