import axios from 'axios'
import { API_URL, JPA_API_URL } from '../../Constants'

class GroupDataService {

    retrieveAllGroups(name) {
        //console.log('executed service')
        return axios.get(`${JPA_API_URL}/users/${name}/groups`);
    }

    retrieveGroup(name, id) {
        //console.log('executed service')
        return axios.get(`${JPA_API_URL}/users/${name}/groups/${id}`);
    }

    deleteGroup(name, id) {
        //console.log('executed service')
        return axios.delete(`${JPA_API_URL}/users/${name}/groups/${id}`);
    }

    updateGroup(name, id, group) {
        //console.log('executed service')
        return axios.put(`${JPA_API_URL}/users/${name}/groups/${id}`, group);
    }

    createGroup(name, group) {
        //console.log('executed service')
        return axios.post(`${JPA_API_URL}/users/${name}/groups/create`, group);
    }

    isJoined(name, id) {
	    return axios.get(`${JPA_API_URL}/users/${name}/groups/${id}/isJoined`);
	}
	
	getNumberOfUsers(name,id) {
		return axios.get(`${JPA_API_URL}/users/${name}/groups/${id}/getNumberOfUsers`);
	}
	
	addUserToStudyGroup(name, id, user) {
		return axios.put(`${JPA_API_URL}/users/${name}/groups/${id}/addUserToStudyGroup`, user);
	}
	
	removeUserFromStudyGroup(name, id, user) {
		return axios.put(`${JPA_API_URL}/users/${name}/groups/${id}/removeUserFromStudyGroup`, user);
	}
	
}

export default new GroupService()
